$(function(){var s=$("#ajax-contact"),t=$("#form-messages");$(s).submit(function(e){e.preventDefault();var a=$(s).serialize();$.ajax({type:"POST",url:$(s).attr("action"),data:a}).done(function(e){$(t).removeClass("error"),$(t).addClass("success"),$(t).text(e),$("#inputName").val(""),$("#inputEmail").val(""),$("#inputMessage").val("")}).fail(function(e){$(t).removeClass("success"),$(t).addClass("error"),""!==e.responseText?$(t).text(e.responseText):$(t).text("Oops! An error occured and your message could not be sent.")})})});